targets.query:
from(bucket: "threshold")
  |> range(start: 0)
  |> filter(fn: (r) => r._measurement == "kikusui" and r.host == "kikusui1")
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> keep(columns: ["_time", "imon", "vmon", "outp", "operation_status", "questionable_status"])
  |> sort(columns: ["_time"], desc: true)
  |> limit(n: 10)
//  |> sort(columns: ["module_channel"], desc: false)
